gauss::~gauss() {
    if (mVcdFile) 
        sc_close_vcd_trace_file(mVcdFile);

    mHdltvinHandle << "] " << endl;
    mHdltvoutHandle << "] " << endl;
    mHdltvinHandle.close();
    mHdltvoutHandle.close();
    delete gauss_bun_1_s_axi_U;
    delete AXIvideo2Mat_U0;
    delete gauss_blur_U0;
    delete Mat2AXIvideo_U0;
    delete img_in_data_stream_0_U;
    delete img_out_data_stream_s_U;
    delete start_for_gauss_blur_U0_U;
    delete start_for_Mat2AXIvideo_U0_U;
}

